mapper.select:
select g.*,a.nickname
from G_GALLERY AS g join members AS a on g.username=a.username
where g_number=#{g_number}

select fg.*, a.nickname
from FREE_GALLERY AS fg  join members as a on fg.username=a.username
where free_number=${free_number}

mapper.update:
UPDATE FREE_GALLERY
SET count = count + 1
WHERE free_number = #{free_number}
