mapper.update:
UPDATE FREE_GALLERY
SET g_count = g_count + 1
WHERE free_number = #{freeNumber}

UPDATE FREE_GALLERY
SET count = count + 1
WHERE free_number = #{freeNumber}

mapper.insert:
insert into comment(coment, coment_date, username, free_number) values(
                                                   #{comment}, now(), #{username},#{free_number}
                                               );
